import React, { Component } from 'react';
import '../App.css';
import ListaJogadoras from './ListaJogadoras';


export default class Escalacao extends Component {
	constructor(props) {
		super(props);

		this.state = {
			formacao: [12],
		};
	}

	render() {
		const { formacao } = this.state;

		return (

			<div className="m-5">

				<div className="header m-5">
					<h1>Escalação</h1>
				</div>

				<div>
					<table className="table my-3 mx-auto">
						<thead>
							<tr>
								<th scope="col">Posição</th>
								<th scope="col">Time</th>
								<th scope="col">Jogadora</th>
								<th scope="col"></th>
							</tr>
						</thead>
						<tbody>
							{formacao &&
								formacao.map((formacao, index) => (
									<tr>
										<td>GOL</td>
										<td>Time 1</td>
										<td>Otto</td>
										<td>
											<div className="btn btn-success btnPlus">+</div>
										</td>
									</tr>
								))}
							<tr>
								<td>GOL</td>
								<td>Time 1</td>
								<td>Otto</td>
								<td>
									<div className="btn btn-success btnPlus">+</div>
								</td>
							</tr>
						</tbody>
					</table>
				</div>
				<hr className="my-4" />
				<div>
					<ListaJogadoras />
				</div>

			</div>

		);
	}
}
