import React, { Component } from "react";
import '../App.css';
import JogadorasDataService from "../services/jogadoras.service";

export default class ListaJogadoras extends Component {
	constructor(props) {
		super(props);
		this.retrieveJogadoras = this.retrieveJogadoras.bind(this);

		this.state = {
			jogadoras: [],
		};
	}

	componentDidMount() {
		this.retrieveJogadoras();
	}


	retrieveJogadoras() {
		JogadorasDataService.getAll()
			.then(response => {
				this.setState({
					jogadoras: response.data
				});
				console.log(response.data);
			})
			.catch(e => {
				console.log(e);
			});
	}

	setFilter(){

	}

	render() {
		const { jogadoras } = this.state;

		return (

			<div>
				<div className="header m-5">
					<h1>Lista Jogadoras</h1>
				</div>
				<div className="my-5">
					<button className="btn btn-primary" type="button" data-toggle="collapse" data-target="#collapseFiltros" aria-expanded="false" aria-controls="collapseExample">
						Filtros
					</button>
					<div className="shadow collapse my-3 mx-0 p-3" id="collapseFiltros">

						<div>
							<div className="mt-2">
								<h5>Posição</h5>
							</div>
						</div>
						<Filtro posicao="Goleira" id="posicaoGOL"/>
						<Filtro posicao="Zagueira" id="posicaoZAG"/>
						<Filtro posicao="Lateral" id="posicaoLAT"/>
						<Filtro posicao="Meia" id="posicaoMEI"/>
						<Filtro posicao="Atacante" id="posicaoATA"/>
					</div>

					<button className="btn btn-primary" type="button">
						Aplicar Filtros
					</button>
				</div>

				<div>
					<table className="table my-3 mx-auto">
						<thead>
							<tr>
								<th scope="col">Posição</th>
								<th scope="col">Time</th>
								<th scope="col">Jogadora</th>
								<th scope="col"></th>
							</tr>
						</thead>
						<tbody>
							{jogadoras &&
								jogadoras.map((jogadoras, index) => (
									<tr>
										<td>{jogadoras.posicaoJogadora}</td>
										<td>{jogadoras.idEquipe}</td>
										<td>{jogadoras.nomeJogadora}</td>
										<td>
											<div className="btn btn-success btnAdicionar">+</div>
										</td>
									</tr>
								))}
						</tbody>
					</table>
				</div>
			</div>

		);
	}
}

class Filtro extends Component {

	render() {

		return (

			<div className="form-check">
				<input className="form-check-input" type="checkbox" value="" id={this.props.id} />
				<label className="form-check-label" for={this.props.id}>
					{this.props.posicao}
				</label>
			</div>


		);
	}
}
