import React, { Component } from 'react';
import '../App.css';
import ListaJogadoras from './ListaJogadoras';


export default class Escalacao extends Component {
	constructor(props) {
		super(props);

		this.state = {
			formacao: [],
			esquema433: ["GOL", "LAT", "ZAG", "ZAG", "LAT", "MEI", "MEI", "MEI", "ATA", "ATA", "ATA"],
			esquema343: ["GOL", "ZAG", "ZAG", "ZAG", "MEI", "MEI", "MEI", "MEI", "ATA", "ATA", "ATA"],
			esquema442: ["GOL", "LAT", "ZAG", "ZAG", "LAT", "MEI", "MEI", "MEI", "MEI", "ATA", "ATA"],
		};
	}

	setFormacao(esquema) {
		if (esquema == "433")
			formacao == esquema433
		else if (esquema == "442")
			formacao == esquema442
		else if (esquema == "343")
			formacao == esquema343
	}

	render() {
		//if (formacao == null)
		//	const { formacao } = this.esquema433;
		//else
		//	const { formacao } = this.formacao;



		return (

			<div className="m-5">

				<div className="header m-5">
					<h1>Escalação</h1>
				</div>

				<div className="form-inline">
					<div>
						<a>Formação</a>
					</div>
					<select className="custom-select m-2">
						<option id="433" onChange={this.setFormacao("433")}>4-3-3</option>
						<option id="442" onChange={this.setFormacao("442")}>4-4-2</option>
						<option id="343" onChange={this.setFormacao("343")}>3-4-3</option>
					</select>
				</div>

				<div>
					<table className="table my-3 mx-auto">
						<thead>
							<tr>
								<th scope="col">Posição</th>
								<th scope="col">Time</th>
								<th scope="col">Jogadora</th>
								<th scope="col"></th>
							</tr>
						</thead>
						<tbody>
							{formacao &&
								formacao.map((formacao, index) => (
									<tr>
										<td>GOL</td>
										<td>Time 1</td>
										<td>Otto</td>
										<td>
											<div className="btn btn-success btnPlus">+</div>
										</td>
									</tr>
								))}
							<tr>
								<td>GOL</td>
								<td>Time 1</td>
								<td>Otto</td>
								<td>
									<div className="btn btn-success btnPlus">+</div>
								</td>
							</tr>
						</tbody>
					</table>
				</div>
				<hr className="my-4" />
				<div>
					<ListaJogadoras />
				</div>

			</div>

		);
	}
}
