import React, { Component } from 'react';
import '../App.css';
import ListaJogadoras from './ListaJogadoras';


export default class Escalacao extends Component {
	constructor(props) {
		super(props);

		this.state = {
			esquema:'4-3-3',
			esquema433: ["GOL", "LAT", "ZAG", "ZAG", "LAT", "MEI", "MEI", "MEI", "ATA", "ATA", "ATA"],
			esquema343: ["GOL", "ZAG", "ZAG", "ZAG", "MEI", "MEI", "MEI", "MEI", "ATA", "ATA", "ATA"],
			esquema442: ["GOL", "LAT", "ZAG", "ZAG", "LAT", "MEI", "MEI", "MEI", "MEI", "ATA", "ATA"],
			formacao: this.state.esquema433,
		};
		this.setEsquema = this.setEsquema.bind(this);
		this.setFormacao = this.setFormacao.bind(this);
	}

	setEsquema(){
		this.setState({esquema: event.target.value});
		this.setFormacao();
	}

	setFormacao() {

		if (this.state.esquema === "4-3-3")
			this.setState({formacao: this.state.esquema433});
		else if (esquema === "4-4-2")
			this.setState({formacao: this.state.esquema442});
		else if (esquema === "3-4-3")
			this.setState({formacao: this.state.esquema343});

	}

	render() {
		const { formacao } = this.state.formacao;

		return (

			<div className="m-5">

				<div className="header m-5">
					<h1>Escalação</h1>
				</div>

				<div className="form-inline">
					<label>
						Formação
          	<select className="custom-select m-2" value={this.state.esquema} onChange={this.handleChange}>            
							<option value="4-3-3">4-3-3</option>
							<option value="4-4-2">4-4-2</option>
							<option value="3-4-3<">3-4-3</option>
						</select>
					</label>
				</div>


				<div>
					<table className="table my-3 mx-auto">
						<thead>
							<tr>
								<th scope="col">Posição</th>
								<th scope="col">Time</th>
								<th scope="col">Jogadora</th>
								<th scope="col"></th>
							</tr>
						</thead>
						<tbody>
							{formacao &&
								formacao.map((formacao, index) => (
									<tr>
										<td>GOL</td>
										<td>Time 1</td>
										<td>Otto</td>
										<td>
											<div className="btn btn-success btnPlus">+</div>
										</td>
									</tr>
								))}
							<tr>
								<td>GOL</td>
								<td>Time 1</td>
								<td>Otto</td>
								<td>
									<div className="btn btn-success btnPlus">+</div>
								</td>
							</tr>
						</tbody>
					</table>
				</div>
				<hr className="my-4" />
				<div>
					<ListaJogadoras />
				</div>

			</div>

		);
	}
}
