import React, { Component } from "react";
import '../App.css';
import JogadorasDataService from "../services/jogadoras.service";

export default class ListaJogadoras extends Component {
	constructor(props) {
		super(props);
		this.retrieveJogadoras = this.retrieveJogadoras.bind(this);
		this.setFilter = this.setFilter.bind(this);
		this.getFilter = this.getFilter.bind(this);

		this.state = {
			jogadoras: [],
			posicaoGOL: false,
			posicaoZAG: false,
			posicaoLAT: false,
			posicaoMEI: false,
			posicaoATA: false,
		};
	}

	componentDidMount() {
		this.retrieveJogadoras();
	}


	retrieveJogadoras() {
		JogadorasDataService.getAll()
			.then(response => {
				this.setState({
					jogadoras: response.data
				});
				console.log(response.data);
			})
			.catch(e => {
				console.log(e);
			});
	}

	setFilter(e){
		this.setState({
			[e.target.id]: e.target.checked,
		})
		console.log("setfilter chamado");
	}

	getFilter(){
		JogadorasDataService.findByFilter("ATA")
		.then(response => {
			this.setState({
				jogadoras: response.data
			});
			console.log(response.data);
		})
		.catch(e => {
			console.log(e);
		});
	}

	render() {
		const { jogadoras } = this.state;

		return (

			<div>
				<div className="header m-5">
					<h1>Lista Jogadoras</h1>
				</div>
				<div className="my-5">
					<button className="btn btn-primary" type="button" data-toggle="collapse" data-target="#collapseFiltros" aria-expanded="false" aria-controls="collapseExample">
						Filtros
					</button>
					<div className="shadow collapse my-3 mx-0 p-3" id="collapseFiltros">

						<div>
							<div className="mt-2">
								<h5>Posição</h5>
							</div>
						</div>
						<Filtro posicao="Goleira" id="posicaoGOL" setFilter={this.setFilter}/>
						<Filtro posicao="Zagueira" id="posicaoZAG"/>
						<Filtro posicao="Lateral" id="posicaoLAT"/>
						<Filtro posicao="Meia" id="posicaoMEI"/>
						<Filtro posicao="Atacante" id="posicaoATA"/>

						<button className="btn btn-primary" type="button" onClick={this.getFilter}>
							Aplicar Filtros
						</button>
					</div>


				</div>

				<div>
					<table className="table my-3 mx-auto">
						<thead>
							<tr>
								<th scope="col">Posição</th>
								<th scope="col">Time</th>
								<th scope="col">Jogadora</th>
								<th scope="col"></th>
							</tr>
						</thead>
						<tbody>
							{jogadoras &&
								jogadoras.map((jogadoras, index) => (
									<tr key={jogadoras.id}>
										<td>{jogadoras.posicaoJogadora}</td>
										<td>{jogadoras.idEquipe}</td>
										<td>{jogadoras.nomeJogadora}</td>
										<td>
											<div className="btn btn-success btnAdicionar">+</div>
										</td>
									</tr>
								))}
						</tbody>
					</table>
				</div>
			</div>

		);
	}
}

class Filtro extends Component {

	render() {

		return (

			<div className="form-check">
				<input className="form-check-input" type="checkbox" value="" id={this.props.id} onChange={this.props.setFilter}/>
				<label className="form-check-label">
					{this.props.posicao}
				</label>
			</div>


		);
	}
}
